    constructor(localService, genral, dialog) {
        this.localService = localService;
        this.genral = genral;
        this.dialog = dialog;
    }
    updateNew() {
        this.setupSource = null;
        this.localService.getItemsSetupTable(this.choosedOne).pipe(take(1)).subscribe(value => {
            this.setupSource = value;
        });
        this.columnsSetup = [
            {
                name: 'value',
                label: $localize `Descrption`,
                search: 'normal',
            },
            {
                name: 'code',
                label: $localize `Code`,
                search: 'normal',
            },
        ];
        this.regConfigTemp = [
            {
                name: 'value',
                label: $localize `Descrption`,
                type: 'input',
            },
            {
                name: 'code',
                label: $localize `Code`,
                type: 'input',
            },
