            }, {
                type: 'checkBool',
                name: 'whole',
                label: $localize `Whole`,
                search: 'none',
            });
            this.regConfigTemp.push({
                name: 'grade',
                label: $localize `Grade`,
                type: 'select',
                options: this.localService.getCashewGrades(),
            }, {
                name: 'saltLevel',
                label: $localize `Salt level`,
                type: 'selectNormal',
                options: this.getSaltLevel(),
            }, {
                type: 'checkbox',
                label: $localize `Whole`,
                name: 'whole',
            });
            if (this.choosedOne.includes('packed')) {
                this.columnsSetup.push({
                    name: 'brand',
                    label: $localize `Brand`,
                    search: 'normal',
                }, {
                    name: 'numBags',
                    label: $localize `Bags in box`,
