                }, {
                    type: 'checkBool',
                    name: 'toffee',
                    label: $localize `Toffee`,
                    search: 'none',
                });
                this.regConfigTemp.push({
                    name: 'brand',
                    label: $localize `Brand`,
                    type: 'input',
                }, {
                    type: 'input',
                    label: $localize `Bags in box`,
                    name: 'numBags',
                    inputType: 'numeric',
                }, {
                    type: 'checkbox',
                    label: $localize `Roast`,
                    name: 'roast',
                }, {
                    type: 'checkbox',
                    label: $localize `Toffee`,
