    this.group.get([this.field.name]).removeAt(index);
    var size = this._vcr.length;
    this.components.splice(index * size, size);
    this.longth--;

    if (index === this.selectedTab) {
      this._vcr.clear();

      for (let i = 0; i < size; i++) {
        this._vcr.insert(this.components[i]);
      }

      this.selectedTab = 0;
    }
  }

